        public void ApplyRotation(Vector3 v)
        {
            if (STMC != null && STMC.GetSelection() == true)
            {
                isEdited = true;
                Matrix m = MyMatrix;
                Vector3 v2 = new Vector3(m.M41, m.M42, m.M43);
                float f = 3.1415f / 180f;
                m.M41 = 0;
                m.M42 = 0;
                m.M43 = 0;
                m *= Matrix.RotationYawPitchRoll(-v.X * f, -v.Y * f, v.Z * f);
                m.M41 = v2.X;
                m.M42 = v2.Y;
                m.M43 = v2.Z;
                MyMatrix = m;
            }
        }

