                    for (int i = 0; i < 12; i++)
                        data[off + i] = buff[i];
                }
                else//have to add prop
                {
                    Console.WriteLine(MyIndex + " : cant find rotation property");
                }
                pcc.Exports[MyIndex].Data = data;
            }
        }

